mapper.select:
select x.id, x.hotel_name, x.hotel_other_name, x.hotel_types, x.todayOrderCount, x.todayCheckInCount, x.todayCheckOutCount, (x.totalCount - (x.todayOrderCount + x.todayCheckInCount))as todayRemindCount, district, address, contract_user, contract_mobile, remark, hotel_password, hotel_username from (select id, hotel_name, hotel_other_name, hotel_types, hotel_password, hotel_username, (select count(1) from order_info where hotel_id = hotel.id and checkin_date = left(now(), 10) and status = '2') as todayOrderCount, (select count(1) from order_info where hotel_id = hotel.id and real_checkin_date = left(now(), 10) and status = '3') as todayCheckInCount, (select count(1) from order_info where hotel_id = hotel.id and checkout_date = left(now(), 10) and status = '5') as todayCheckOutCount, (select sum(house1_total_count + house2_total_count + house3_total_count + house4_total_count + house5_total_count + house6_total_count) from hotel b where b.id = hotel.id) as totalCount, district, address, contract_user, contract_mobile, remark from hotel where 1=1
and district = #{district}
and street = #{street}
and deleted = #{deleted}
and notshow = #{notshow}
and hotel_types like '%${hotelTypes}%'
and hotel_name like '%${hotelName}%'
) x;
